sys_ui_action.script:
function checkForSkippedRecords() {
	var params = {};
	params.sysId = g_form.getUniqueValue();
	if(g_form.getValue("scope")== "global") {
		var ga = new GlideAjax("ScopedAppUploaderAJAX");
		ga.addParam("sysparm_name", "getStolenFilesDetails");
		ga.addParam("sysparm_app_scope", g_form.getUniqueValue()); // sys_id in case of global apps
		ga.getXML(openRelevantModal);
	} else {
		uploadToInstance(params);
	}

}

function openRelevantModal(response) {
	var params = {};
	params.sysId = g_form.getUniqueValue();
	params.appName = g_form.getValue("name");
	params.scope = g_form.getValue("scope");
	params.numberOfStolenFilesForApp = parseInt(response.responseXML.getElementsByTagName("number_of_stolen_files_for_app")[0].getAttribute("value"));
	params.viewDetailsLink = response.responseXML.getElementsByTagName("view_details_link")[0].getAttribute("value");

	if(params.numberOfStolenFilesForApp > 0 && params.viewDetailsLink)
		renderSkippedRecordsWarningDialog(params);
	else
		uploadToInstance(params);
}

function uploadToInstance(params) {
    var map = new GwtMessage().getMessages(["Publish to My Application Repository"]);
    var dd = new GlideModal("sn_appauthor_app_upload_to_repo");
    dd.setTitle(map["Publish to My Application Repository"]);
    dd.setPreference('sysparm_sys_id', params.sysId);
	dd.setPreference('sysparm_publish_to_app_store', 'false');
    dd.setWidth(500);
    dd.render();
}

function renderSkippedRecordsWarningDialog(params) {
	var skippedRecordsWarningDialog = new GlideModal("sn_appauthor_skipped_records_warning_dialog");
	skippedRecordsWarningDialog.setTitle("Publish");
	skippedRecordsWarningDialog.setPreference("sysparm_app_name", params.appName);
	skippedRecordsWarningDialog.setPreference("sysparm_view_details_link", params.viewDetailsLink);
	skippedRecordsWarningDialog.setPreference("sysparm_number_of_conflicts_for_publish", params.numberOfStolenFilesForApp);
	skippedRecordsWarningDialog.setPreference("sysparm_sys_id", params.sysId);
	skippedRecordsWarningDialog.on("continueAppPublish", function() {
		uploadToInstance(params);
	});
	skippedRecordsWarningDialog.render();
}
